/* global bstUI, bst */

import React, { useEffect } from 'react';
import BinarySearchTree from './impl';  // Import BinarySearchTree
import BinarySearchTreeUI, { defaultBSTUIConfig } from './bst-ui';

const BST = () => {
  // Declare bstUI at a higher scope
  let bstUI;

  useEffect(() => {
    // Instantiate BinarySearchTree and BinarySearchTreeUI
    const bst = new BinarySearchTree();
    bstUI = new BinarySearchTreeUI(bst);

    // Initialize the UI
    bstUI.init();

    // Insert some initial values (optional)
    bst.insert(50);
    bst.insert(30);
    bst.insert(70);
    bst.insert(20);
    bst.insert(40);
    bst.insert(60);
    bst.insert(80);

    // Render the initial tree
    bstUI.render(bst.root);
  }, []);

  return (
    <div>
      <h1>Binary Search Tree Component</h1>
      <div className="tree">
        {/* Container for rendering the binary search tree */}
        {bstUI && bstUI.template()} {/* Include UI buttons generated by template method */}
        {bstUI && bstUI.getTreeUI(bst.root)} {/* Render the binary search tree */}
      </div>
      <div className="bst-actions-container">
        {/* Container for UI buttons and actions */}
      </div>
    </div>
  );
};

export default BST;
