            }
            currentnode=currentnode.children[char];
        }
        return currentnode.isEndofword;

    }
}

class TrieRenderer {
    constructor() {
        //treeContainerSelector = '.rb-tree';
        actionsContainerSelector = '.rb-actions-container';
        this.trieContainerSelector = '#trie-container';
    }

    onInsertTrieClick = async () => {
        const element = await this.promptAsync('Enter word to insert into the trie');
        if (!element) {
            return;
        }

        this.insert(element);
        this.renderTrie();
    };

    promptAsync = (promptMessage) => {
        return new Promise(resolve => {
            const userInput = prompt(promptMessage);
            resolve(userInput);
        });
    };

    renderTrie = () => {
        const trieContainer = document.querySelector(this.trieContainerSelector);
        if (!this.root) {
            return (trieContainer.innerHTML = '');
        }
        const template = this.getTrieUI(this.root);
        trieContainer.innerHTML = template;
    };

    getTrieUI = (node, value = '') => {
        const { children } = node;

        if (!node) {
            return '';
        }

        let childrenUI = '';
        for (const char in children) {
            if (children.hasOwnProperty(char)) {
                childrenUI += this.getTrieUI(children[char], char);
            }
        }

        return `
            <div class="node__element" data-node-id="${value}">${value}</div>
            ${
                childrenUI
                    ? `
                    <div class="node__bottom-line"></div>
                    <div class="node__children">
                        ${childrenUI}
                    </div>
                    `
                    : ''
            }
        `;
    };
}

