export const defaultMinHeapUIConfig = {
    HIGHLIGHT_CLASS: 'node__element--highlight',
    HIGHLIGHT_TIME: 300,
  };

  class MinHeapUI {
    highlightTimer = null;
    actionsContainerSelector;

    constructor(
      heap,
      render,
      heapContainerSelector = '.heap',
      actionsContainerSelector = '.min-heap-actions-container',
      config = {
        HIGHLIGHT_CLASS: 'node__element--highlight',
        HIGHLIGHT_TIME: 800,
      }
    ) {
      this.heapContainerSelector = heapContainerSelector;
      this.actionsContainerSelector = actionsContainerSelector;
      this.config = config;
      this.heap = heap;
      this.render = render || this.renderHeap;
      const root = document.documentElement;
      root.style.setProperty(
        '--animation-timing',
        `${this.config.HIGHLIGHT_TIME / 1000}s`
      );
    }

    template() {
      return `
        <div class="btn-group">
          <button id="heapinsert" class="btn btn-warning">
            Insert Element
          </button>
          <button id="extractMinBtn" class="btn btn-dark">
            Extract Min
          </button>
        </div>
        <div class="btn-group">
          <button id="peekBtn" class="btn btn-primary">Peek</button>
        </div>
        <div class="btn-group">
          <button id="resetBtn" class="btn btn-danger">
            Delete Heap
          </button>
        </div>
      `;
    }

    renderHeap(containerSelector = this.heapContainerSelector) {
      const heapContainer = document.querySelector(containerSelector);
      if (!heapContainer) return; // Exit if container is not found

      // Clear the container first
      heapContainer.innerHTML = '';

      // Render the heap recursively starting from the root
      heapContainer.appendChild(this.createHeapNodeUI());
    }

    createHeapNodeUI(index = 0) {
      const { items } = this.heap;

      if (index >= items.length) {
        return null; // Return null if index exceeds heap size
      }

      const currentValue = items[index];
      const nodeElement = document.createElement('div');
      nodeElement.classList.add('node__element', 'heap-node');
      nodeElement.setAttribute('data-index', index);
      nodeElement.textContent = currentValue;

      const leftChildIndex = 2 * index + 1;
      const rightChildIndex = 2 * index + 2;

      const leftChildUI = this.createHeapNodeUI(leftChildIndex);
      const rightChildUI = this.createHeapNodeUI(rightChildIndex);

      if (leftChildUI || rightChildUI) {
        const bottomLine = document.createElement('div');
        bottomLine.classList.add('node__bottom-line');

        const childrenNode = document.createElement('div');
        childrenNode.classList.add('node__children');

        if (leftChildUI) {
          const leftNode = document.createElement('div');
          leftNode.classList.add('node', 'node--left', 'heap-children');
          leftNode.appendChild(leftChildUI);
          childrenNode.appendChild(leftNode);
        }

        if (rightChildUI) {
          const rightNode = document.createElement('div');
          rightNode.classList.add('node', 'node--right', 'heap-children');
          rightNode.appendChild(rightChildUI);
          childrenNode.appendChild(rightNode);
        }

        nodeElement.appendChild(bottomLine);
        nodeElement.appendChild(childrenNode);
      }

      return nodeElement;
    }

    highlightNode(index) {
      const nodeElement = document.querySelector(`[data-index="${index}"]`);

      if (this.highlightTimer !== null) {
        clearTimeout(this.highlightTimer);
        nodeElement.classList.remove(this.config.HIGHLIGHT_CLASS);
        this.highlightTimer = null;
        return;
      }

      nodeElement.classList.add(this.config.HIGHLIGHT_CLASS);

      return new Promise((resolve) => {
        this.highlightTimer = setTimeout(() => {
          nodeElement.classList.remove(this.config.HIGHLIGHT_CLASS);
          this.highlightTimer = null;
          resolve();
        }, this.config.HIGHLIGHT_TIME);
      });
    }

    onInsertClick() {
      const element = prompt('Enter element to insert into the heap');
      if (!element) {
        return;
      }
      this.heap.insert(parseInt(element));
      this.render();
    }

    onExtractMinBtnClick() {
      const min = this.heap.extractMin();
      if (min !== null) {
        this.render();
      } else {
        alert('Heap is empty');
      }
    }

    onPeekBtnClick() {
      const min = this.heap.peek();
      if (min !== null) {
        alert(`Min element: ${min}`);
      } else {
        alert('Heap is empty');
      }
    }

    onResetBtnClick() {
      this.heap.clear();
      this.render();
    }

    setTemplate() {
      const actionsContainer = document.querySelector(
        this.actionsContainerSelector
      );
      actionsContainer.innerHTML = this.template();
    }

    init() {
      this.setTemplate();
      const insertbutton = document.querySelector('#heapinsert');
      const extractMinBtn = document.querySelector('#extractMinBtn');
      const peekBtn = document.querySelector('#peekBtn');
      const resetBtn = document.querySelector('#resetBtn');

      insertbutton.addEventListener('click', this.onInsertClick.bind(this));
      extractMinBtn.addEventListener(
        'click',
        this.onExtractMinBtnClick.bind(this)
      );
      peekBtn.addEventListener('click', this.onPeekBtnClick.bind(this));
      resetBtn.addEventListener('click', this.onResetBtnClick.bind(this));
    }
  }

  export default MinHeapUI;
