import React, { useState } from 'react';
import './App.css';
import BST from './BST/bst'; // Import your BST component
import RedBlackTree from './RB/rb'; // Import your Red-Black Tree component
import AVLTree from './AVL/avl'; // Import your AVL Tree component

function App() {
  const [showBST, setShowBST] = useState(false);
  const [showRedBlack, setShowRedBlack] = useState(false);
  const [showAVL, setShowAVL] = useState(false);

  const handleBSTClick = () => {
    setShowBST(true);
    setShowRedBlack(false);
    setShowAVL(false);
  };

  const handleRedBlackClick = () => {
    setShowBST(false);
    setShowRedBlack(true);
    setShowAVL(false);
  };

  const handleAVLClick = () => {
    setShowBST(false);
    setShowRedBlack(false);
    setShowAVL(true);
  };

  return (
    <div className="App">
      <button onClick={handleBSTClick}>BST</button>
      <button onClick={handleRedBlackClick}>Red-Black</button>
      <button onClick={handleAVLClick}>AVL</button>

      {showBST && <BST />}
      {showRedBlack && <RedBlackTree />}
      {showAVL && <AVLTree />}
    </div>
  );
}

export default App;
