class TrieNode {
    constructor() {
    this. children = {};
    this.isEndofword = false;
    }
}

class Trie{
    constructor() {
        this.root=new TrieNode();

    }
    insert(word)
    {
        let currentnode=this.root;
        for(let i=0;i<word.length;i++)
        {
            const char=word[i];
            if(!currentnode.children[char])
            {
                currentnode.children[char]=new TrieNode();
            }
            currentnode=currentnode.children[char];
        }
        currentnode.isEndofword=true;

    }
    search(word)
    {
        let currentnode=this.root;
        for(let i=0;i<word.length;i++)
        {
            const char=word[i];
            if(!currentnode.children[char])
            {
                return false;
            }
            currentnode=currentnode.children[char];
        }
        return currentnode.isEndofword;

    }
}

class TrieRenderer {
    constructor() {
        //treeContainerSelector = '.rb-tree';
        this.actionsContainerSelector = '.rb-actions-container';
        this.trieContainerSelector = '#trie-container';
    }
    template() {
        return `
        <div class="btn-group">
          <button id="insertrbnode" class="btn btn-warning">
            Insert Node
          </button>
          <button id="removerbnode" class="btn btn-dark">
            Remove Node
          </button>
        </div>
        <div class="btn-group">
          <button id="deleterbtree" class="btn btn-danger">
            Delete Tree
          </button>
        </div>
          <div class="btn-group">
            <button id="PreviewBtn" hidden="true" class="btn btn-dark">
              Hide Balance Tree Preview
            </button>
          </div>
        `;
      }
      setTemplate() {
        const actionsContainer = document.querySelector(
          this.actionsContainerSelector
        );
        actionsContainer.innerHTML = this.template();
      }

      init() {
        this.setTemplate();
        const insert = document.querySelector('#insertrbnode');
        const removeElementBtn = document.querySelector('#removerbnode');
        const delBtn = document.querySelector('#deleterbtree');
        // const toggleBalancePreviewBtn = document.querySelector('#PreviewBtn');
        insert.addEventListener('click', this.onInsertTrieClick.bind(this));
        removeElementBtn.addEventListener('click', this.onRemoverbClick.bind(this));
        delBtn.addEventListener('click', this.onResetrbClick.bind(this));
        // toggleBalancePreviewBtn.addEventListener('click', () => {this.resetBalancedTreeView();});
      }

    onInsertTrieClick = async () => {
        const element = await this.promptAsync('Enter word to insert into the trie');
        if (!element) {
            return;
        }

        this.insert(element);
        this.renderTrie();
    };

    promptAsync = (promptMessage) => {
        return new Promise(resolve => {
            const userInput = prompt(promptMessage);
            resolve(userInput);
        });
    };

    renderTrie = () => {
        const trieContainer = document.querySelector(this.trieContainerSelector);
        if (!this.root) {
            return (trieContainer.innerHTML = '');
        }
        const template = this.getTrieUI(this.root);
        trieContainer.innerHTML = template;
    };

    getTrieUI = (node, value = '') => {
        const { children } = node;

        if (!node) {
            return '';
        }

        let childrenUI = '';
        for (const char in children) {
            if (children.hasOwnProperty(char)) {
                childrenUI += this.getTrieUI(children[char], char);
            }
        }

        return `
            <div class="node__element" data-node-id="${value}">${value}</div>
            ${
                childrenUI
                    ? `
                    <div class="node__bottom-line"></div>
                    <div class="node__children">
                        ${childrenUI}
                    </div>
                    `
                    : ''
            }
        `;
    };
}

export { Trie, TrieRenderer };
