      super(...arguments);
    //   this.tree = new RedBlackTree();
      this.treeContainerSelector = '.rb-tree';
      this.actionsContainerSelector = '.rb-actions-container';
    }
    template() {
        return `
        <div class="btn-group">
          <button id="insertrbnode" class="btn btn-warning">
            Insert Node
          </button>
          <button id="removerbnode" class="btn btn-dark">
            Remove Node
          </button>
        </div>
        <div class="btn-group">
          <button id="deleterbtree" class="btn btn-danger">
            Delete Tree
          </button>
        </div>
          <div class="btn-group">
            <button id="PreviewBtn" hidden="true" class="btn btn-dark">
              Hide Balance Tree Preview
            </button>
          </div>
        `;
      }

      init() {
        this.setTemplate();
        const insert = document.querySelector('#insertrbnode');
        const removeElementBtn = document.querySelector('#removerbnode');
        const delBtn = document.querySelector('#deleterbtree');
        // const toggleBalancePreviewBtn = document.querySelector('#PreviewBtn');
        insert.addEventListener('click', this.onInsertrbClick.bind(this));
        removeElementBtn.addEventListener('click', this.onRemoverbClick.bind(this));
        delBtn.addEventListener('click', this.onResetrbClick.bind(this));
        // toggleBalancePreviewBtn.addEventListener('click', () => {this.resetBalancedTreeView();});
      }
      onInsertrbClick() {
        const element = prompt('Enter element to insert into the tree');
        if (!element) {
            return;
        }

        if (this.tree.search(element)) {
            alert('Element already exists in the tree.');
            return;
        }

        // Insert into the existing tree
        console.log("before insert: " + element);
        this.tree.insert(element);  // Use this.tree.insert to call the insert method from RedBlackTree

        // Render the tree using the existing tree instance
        this.renderrbTree(this.tree.root);
        // this.highlightNode(insertedNode); // You can add this line if needed
    }


    onRemoverbClick() {
        const element = prompt('Enter element to remove from the tree');
        if (!element) {
            return;
        }
        const elementExists = this.tree.search(element);
        if (!elementExists) {
            alert('Element not found');
            return;
        }

        // Use remove instead of removerb
        const removedEl = this.tree.remove(element);
        if (removedEl) {
            this.highlightNode(removedEl).then(() => {
                this.render(this.tree.root);
            });
        }
    }

    onResetrbClick() {
        if (!this.tree.root) {
            alert('Create a tree to delete');
            return;
        }
        this.highlightNode(this.tree.root).then(() => {
            this.tree.root = null;
            this.renderrbTree(this.tree.root);
        });
    }

      setTemplate() {
        const actionsContainer = document.querySelector(
          this.actionsContainerSelector
        );
        actionsContainer.innerHTML = this.template();
      }

      renderrbTree(
        node = this.tree.root,
        containerSelector = this.treeContainerSelector
    ) {
        const treeContainer = document.querySelector(containerSelector);
        if (!node) {
            return (treeContainer.innerHTML = '');
        }
        const template = this.getrbTreeUI(node);
        treeContainer.innerHTML = template;

        // Add event listeners or additional styling logic here if needed
    }

    getrbTreeUI(node) {
        if (!node) {
            return '';
        }

        // Determine the color class based on the node's color attribute
        const colorClass = node.color ? node.color.toLowerCase() : '';

        // Use the colorClass in your HTML template
        const template = `
            <div class="node ${colorClass}">
                ${node.value}
            </div>
            <div class="tree-node">
                ${this.getTreeUI(node.left)}
            </div>
            <div class="tree-node">
                ${this.getTreeUI(node.right)}
            </div>
        `;

        return template;
    }




}
