import BinarySearchTree from "../BST/js_binary_search_tree";
const BLACK = 'BLACK';
const RED = 'RED';

class Node {
    constructor(data, color = RED) {
        this.value = data;
        this.color = color;
        this.left = null;
        this.right = null;
        this.parent = null;
    }


  isLeftChild() {
      return this.parent && this.parent.left === this;
  }

  flipColor() {
      this.color = this.color === BLACK ? RED : BLACK;
  }
}

class RedBlackTree extends BinarySearchTree{
  constructor() {
    super();
  }

  insert(data) {
    const node = new Node(data, RED);
    this.root = this._insert(this.root, node);
    this._recolorAndRotate(node);   
}


  _insert(currentNode, newNode) {
      if (currentNode === null) {
          return newNode;
      }

      if (newNode.data < currentNode.data) {
          currentNode.left = this._insert(currentNode.left, newNode);
          currentNode.left.parent = currentNode;
      } else if (newNode.data > currentNode.data) {
          currentNode.right = this._insert(currentNode.right, newNode);
          currentNode.right.parent = currentNode;
      }

      return currentNode;
  }

  _recolorAndRotate(node) {
      const parent = node.parent;
      if (node !== this.root && parent.color === RED) {
          const grandParent = parent.parent;
          const uncle = parent.isLeftChild() ? grandParent.right : grandParent.left;

          if (uncle !== null && uncle.color === RED) {
              this._handleRecoloring(parent, uncle, grandParent);
          } else if (parent.isLeftChild()) {
              this._handleLeftSituations(node, parent, grandParent);
          } else {
              this._handleRightSituations(node, parent, grandParent);
          }
      }

      this.root.color = BLACK;
  }

  _handleRightSituations(node, parent, grandParent) {
      if (node.isLeftChild()) {
          this._rotateRight(parent);
      }
      parent.flipColor();
      grandParent.flipColor();
      this._rotateLeft(grandParent);
      this._recolorAndRotate(node.isLeftChild() ? grandParent : parent);
  }

  _handleLeftSituations(node, parent, grandParent) {
      if (!node.isLeftChild()) {
          this._rotateLeft(parent);
      }
      parent.flipColor();
      grandParent.flipColor();
      this._rotateRight(grandParent);
      this._recolorAndRotate(node.isLeftChild() ? parent : grandParent);
  }

  _handleRecoloring(parent, uncle, grandParent) {
      uncle.flipColor();
      parent.flipColor();
      grandParent.flipColor();
      this._recolorAndRotate(grandParent);
  }

  _rotateRight(node) {
      const leftNode = node.left;
      node.left = leftNode.right;

      if (node.left !== null) {
          node.left.parent = node;
      }

      leftNode.right = node;
      leftNode.parent = node.parent;
      this._updateChildrenOfParentNode(node, leftNode);
      node.parent = leftNode;
  }

  _rotateLeft(node) {
      const rightNode = node.right;
      node.right = rightNode.left;

      if (node.right !== null) {
          node.right.parent = node;
      }

      rightNode.left = node;
      rightNode.parent = node.parent;
      this._updateChildrenOfParentNode(node, rightNode);
      node.parent = rightNode;
  }

  _updateChildrenOfParentNode(node, tempNode) {
      if (node.parent === null) {
          this.root = tempNode;
      } else if (node.isLeftChild()) {
          node.parent.left = tempNode;
      } else {
          node.parent.right = tempNode;
      }
  }

  traverse() {
      this._traverseInOrder(this.root);
  }

  _traverseInOrder(node) {
      if (node !== null) {
          this._traverseInOrder(node.left);
          console.log(node.data);
          this._traverseInOrder(node.right);
      }
  }

  getMax() {
      if (this.isEmpty()) {
          return null;
      }
      return this._getMax(this.root);
  }

  _getMax(node) {
      if (node.right !== null) {
          return this._getMax(node.right);
      }
      return node.data;
  }

  getMin() {
      if (this.isEmpty()) {
          return null;
      }
      return this._getMin(this.root);
  }

  _getMin(node) {
      if (node.left !== null) {
          return this._getMin(node.left);
      }
      return node.data;
  }

  isEmpty() {
      return this.root === null;
  }
}
export default RedBlackTree;
